INSERT INTO `wp_options` VALUES("2426", "_transient_dash_c05853b002c443ec8e57ff884f56cdde", "<div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://es.wordpress.org/2017/05/02/noticias-y-eventos-wordpress-para-la-semana-18-17/\'>Noticias y eventos de la semana</a> <span class=\"rss-date\">2 Mayo, 2017</span><div class=\"rssSummary\">¡Buenos días a todos! Os dejamos algunas noticias, el listado de las Meetups para esta semana y también el avance de las organizaciones de las distintas WordCamps. Meetups 02/05 – Chiclana – Crea páginas web de manera sencilla con WordPress para dar a conocer tu afición 03/05 – Sevilla – WP Feria de Sevilla 2017. Rebujito Reloaded (¡Qué envidia! 💃🏼) [&hellip;]</div></li></ul></div><div class=\"rss-widget\"><ul><li><a class=\'rsswidget\' href=\'https://ma.tt/2017/05/longreads-and-original-journalism/\'>Matt: Longreads and Original Journalism</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/wp-elevation-is-focusing-all-content-on-mental-health-for-month-of-may\'>WPTavern: WP Elevation is Focusing All Content on Mental Health for Month of May</a></li><li><a class=\'rsswidget\' href=\'https://wptavern.com/10up-releases-wp-docker-an-open-source-docker-configuration-for-local-wordpress-development\'>WPTavern: 10up Releases WP Docker, an Open Source Docker Configuration for Local WordPress Development</a></li></ul></div><div class=\"rss-widget\"><ul><li class=\"dashboard-news-plugin\"><span>Plugin popular:</span> WordPress Importer&nbsp;<a href=\"plugin-install.php?tab=plugin-information&amp;plugin=wordpress-importer&amp;_wpnonce=0200f04641&amp;TB_iframe=true&amp;width=600&amp;height=800\" class=\"thickbox open-plugin-details-modal\" aria-label=\"Instalar WordPress Importer\">(Instalar)</a></li></ul></div>", "no");
