INSERT INTO `wp_posts` VALUES("19", "1", "2017-04-02 14:13:20", "2017-04-02 14:13:20", "<h2>Especificaciones:</h2>\r\n<ul>\r\n 	<li class=\"ms-hover\">Exhibición\r\n<ul>\r\n 	<li class=\"ms-hover\">Diagonal de la pantalla: 95,2 cm (37.5\")</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Brillo de pantalla: 300 cd / m²</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Resolución de la pantalla: 3840 x 1600 Pixeles</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Tiempo de respuesta: 14 ms</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Pantalla: LED</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Tipo HD: Full HD</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Tecnología de visualización: IPS</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Formatos gráficos soportados: 3840 x 1600</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Nombre comercial de la relación de contraste dinámico: Mega Contrast</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Ángulo de visión, horizontal: 178°</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Ángulo de visión, vertical: 178°</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Número de colores de la pantalla: 1.073 billones de colores</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Relación de aspecto nativa: 21:9</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Diagonal de pantalla: 95,25 cm</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Tamaño de pixel: 0,229 x 0,229 mm</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Máxima velocidad de actualización: 60 Hz</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">sRGB: Si</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">HDCP: Si</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Formato de vídeo soportado: 1080p</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Gama de colores: 99%</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">DDC / CI: Si</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Forma de la pantalla: Curva</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Puertos e Interfaces\r\n<ul>\r\n 	<li class=\"ms-hover\">Conector USB incorporado: Si</li>\r\n</ul>\r\n<ul>\r\n 	<li>Versión de conector USB: USB 3.0 (3.1 Gen 1)</li>\r\n</ul>\r\n<ul>\r\n 	<li>Tipo de puerto USB de carga: USB Type-B</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Cantidad de puertos USB descendentes de tipo A: 2</li>\r\n</ul>\r\n<ul>\r\n 	<li>Cantidad de puertos downstream USB tipo C: 1</li>\r\n</ul>\r\n<ul>\r\n 	<li>Cantidad de DisplayPorts: 1</li>\r\n</ul>\r\n<ul>\r\n 	<li>Salida de audio: Si</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Versión USB: 3.0 (3.1 Gen 1)</li>\r\n</ul>\r\n<ul>\r\n 	<li>Entrada de CA: Si</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Peso y dimensiones\r\n<ul>\r\n 	<li>Ancho: 896,6 mm</li>\r\n</ul>\r\n<ul>\r\n 	<li>Profundidad: 91,4 mm</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Altura: 393,7 mm</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Peso: 7,67 kg</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Ancho del dispositivo (con soporte): 83,1 cm</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Profundidad dispositivo (con soporte): 27,9 cm</li>\r\n</ul>\r\n<ul>\r\n 	<li>Altura del dispositivo (con soporte): 45 cm</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Peso con stand: 9,3 kg</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Ancho del paquete: 105,4 cm</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Profundidad del paquete: 20,6 cm</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Altura del paquete: 53,8 cm</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Peso del paquete: 13,2 kg</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Control de energía\r\n<ul>\r\n 	<li>Voltaje de entrada AC: 100-240 V</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Frecuencia de entrada AC: 50/60 Hz</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Consumo energético: 120 W</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Detalles técnicos\r\n<ul>\r\n 	<li>Tipo de visualizador: IPS</li>\r\n</ul>\r\n<ul>\r\n 	<li>Modos de pantalla: Picture-in-Picture</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Modo de juego: Si</li>\r\n</ul>\r\n<ul>\r\n 	<li>Puerto de carga USB: Si</li>\r\n</ul>\r\n<ul>\r\n 	<li>Concentrador USB: Si</li>\r\n</ul>\r\n<ul>\r\n 	<li>Número de colores: 1.073 billones de colores</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Red\r\n<ul>\r\n 	<li class=\"ms-hover\">Bluetooth: Si</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li>Aprobaciones reguladoras\r\n<ul>\r\n 	<li>Certificación: UL(cUL) TUV-type FCC-B, CE BSMI (for Taiwan) CCC (for China) Windows Erp</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Ergonomía\r\n<ul>\r\n 	<li>montaje VESA: Si</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Interfaces de montaje VESA: 100 x 100 mm</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Ajuste de la inclinación: Si</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Ángulo de inclinación: -5 - 15°</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Ajustes de altura: Si</li>\r\n</ul>\r\n<ul>\r\n 	<li>Ajuste de altura: 11 cm</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Interruptor de encendido/apagado integrado: Si</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Contenido del embalaje\r\n<ul>\r\n 	<li class=\"ms-hover\">Stand incluido: Si</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Diseño\r\n<ul>\r\n 	<li class=\"ms-hover\">Color del producto: Color blanco</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Multimedia\r\n<ul>\r\n 	<li>Altavoces incorporados: Si</li>\r\n</ul>\r\n<ul>\r\n 	<li>Número de altavoces: 2</li>\r\n</ul>\r\n<ul>\r\n 	<li>Potencia estimada RMS: 20 W</li>\r\n</ul>\r\n</li>\r\n</ul>", "LG 38UC99-W 38\" LED IPS Ultrawide Blanco", "Te presentamos el fantástico monitor 38UC99 38\" Ultrawide de LG.", "publish", "open", "closed", "", "lg-38uc99-w-38-led-ips-ultrawide-blanco", "", "", "2017-04-10 12:03:40", "2017-04-10 12:03:40", "", "0", "http://localhost/GCM/wordpress/?post_type=product&#038;p=19", "0", "product", "", "0");
