INSERT INTO `wp_posts` VALUES("29", "1", "2017-04-02 14:21:41", "2017-04-02 14:21:41", "<h2>Especificaciones</h2>\r\n<ul>\r\n 	<li class=\"ms-hover\">Puertos e Interfaces\r\n<ul>\r\n 	<li class=\"ms-hover\">Tecnología de conectividad: Alámbrico/Inalámbrico</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Interfaz del dispositivo: RF Wireless + USB</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Alcance inalámbrico: 10 m</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Peso y dimensiones\r\n<ul>\r\n 	<li class=\"ms-hover\">Peso: 380 g</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Detalles técnicos\r\n<ul>\r\n 	<li class=\"ms-hover\">Conexión a PC: USB 2.0</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li class=\"ms-hover\">Micrófono\r\n<ul>\r\n 	<li>Tipo de dirección de micrófono: Uni</li>\r\n</ul>\r\n<ul>\r\n 	<li>Micrófono mudo: Si</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li>Auriculares\r\n<ul>\r\n 	<li>Audifonos: Circumaural</li>\r\n</ul>\r\n<ul>\r\n 	<li>Frecuencia de auricular: 20 - 20000 Hz</li>\r\n</ul>\r\n<ul>\r\n 	<li>Obstrucción: 48 ?</li>\r\n</ul>\r\n<ul>\r\n 	<li>Sensibilidad de auricular: 118 dB</li>\r\n</ul>\r\n<ul>\r\n 	<li>Tipo de imán: Neodimio</li>\r\n</ul>\r\n<ul>\r\n 	<li>Unidad de disco: 4 cm</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li>Batería\r\n<ul>\r\n 	<li>Autonomía: 15 h</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li>Diseño\r\n<ul>\r\n 	<li>Tipo de auricular: Binaurale</li>\r\n</ul>\r\n<ul>\r\n 	<li>Estilo de uso: Diadema</li>\r\n</ul>\r\n<ul>\r\n 	<li>Color del producto: Negro, Azul</li>\r\n</ul>\r\n<ul>\r\n 	<li>Productos compatibles: PS3. PS4, PC, Mac</li>\r\n</ul>\r\n</li>\r\n</ul>\r\n<ul>\r\n 	<li>Desempeño\r\n<ul>\r\n 	<li>Utilizar con: PC/Juegos</li>\r\n</ul>\r\n</li>\r\n</ul>", "ASTRO Gaming A50 Auriculares Inalámbricos para PC/PS4", "Te presentamos los Auriculares Inalámbricos A50 de Astro Gaming. Experimenta la inmersión absoluta y un rendimiento excelente sin el inconveniente y las restricciones de los cables. ¡Libertad sin ataduras!", "publish", "open", "closed", "", "astro-gaming-a50-auriculares-inalambricos-para-pcps4", "", "", "2017-04-10 12:08:50", "2017-04-10 12:08:50", "", "0", "http://localhost/GCM/wordpress/?post_type=product&#038;p=29", "0", "product", "", "0");
